Scripts.OnEnter:
self.Name:SetTextColor(0.4,1,0)
self.Delete:SetAlpha(1)

GameTooltip:SetOwner(self:GetParent(), "ANCHOR_NONE")
GameTooltip:SetPoint("TOPLEFT", self:GetParent(), "TOPRIGHT", 5, -5);
GameTooltip:SetSpellByID(self.spellID);
GameTooltip:AddDivider()
GameTooltip:AddLine(format(SYNCUI_STRING_AURAID, self.spellID),1,1,1)
GameTooltip:Show();
